const { Util } = require("./utils");

const util = new Util();

const allowedRole = (roles) => {
  return (req, res, next) => {
    const userRole = req?.user?.role;
    if (roles.includes(userRole) || req.user.email === process.env.EMAIL) {
      next();
    } else {
      util.statusCode = 400;
      util.message = "You are not allowed to perform this action";
      return util.send(res);
    }
  };
};
module.exports = allowedRole;

const fs = require("fs");

// Middleware function to log API calls
function apiLogger(req, res, next) {
  const { method, url, body } = req;
  const metadata = {
    route: req.originalUrl,
    buttonClicked: req.headers["button-clicked"],
    // Add more metadata as needed
  };
  const logData = {
    timestamp: new Date().toISOString(),
    method,
    url,
    metadata,
    body,
  };
  const logString = JSON.stringify(logData);

  // Write log to file
  fs.appendFile("api_logs.txt", logString + "\n", (err) => {
    if (err) {
      console.error("Error writing log:", err);
    }
  });

  next();
}

module.exports = apiLogger;

const nodemailer = require("nodemailer");
const dotenv = require("dotenv");

dotenv.config();

const transporter = nodemailer.createTransport({
  service: "gmail",
  auth: {
    user: process.env.EMAIL,
    pass: process.env.PASSWORD,
  },
});

module.exports = transporter;

const admin = require("firebase-admin");

async function sendNotification({ token, title, body, userId }) {
  try {
    await admin.messaging().send({
      token,
      notification: {
        title,
        body,
      },
    });
    const notificationData = {
      title,
      body,
      timestamp: admin.firestore.FieldValue.serverTimestamp(),
    };
    await admin
      .firestore()
      .collection("users")
      .doc(userId)
      .collection("notifications")
      .add(notificationData);

    return {
      statusCode: 200,
      message: "Notification sent successfully",
    };
  } catch (error) {
    return {
      statusCode: 500,
      message: error.message || "Failed to send notification",
    };
  }
}

module.exports = sendNotification;

class Util {
  constructor() {
    this.statusCode = null;
    this.type = null;
    this.data = null;
    this.message = null;
  }

  setSuccess(statusCode, message, data) {
    this.statusCode = statusCode;
    this.message = message;
    this.data = data;
    this.type = "success";
  }

  setError(statusCode, message) {
    this.statusCode = statusCode;
    this.message = message;
    this.type = "error";
  }

  send(res) {
    const result = {
      status: this.statusCode,
      message: this.message,
      data: this.data,
    };
    if (this.type === "success") {
      return res.status(this.statusCode).json(result);
    }
    return res.status(this.statusCode).json({
      status: this.statusCode,
      message: this.message,
    });
  }
}

module.exports.Util = Util;

const express = require("express");
const dotenv = require("dotenv");
const cors = require("cors");
const functions = require("firebase-functions");
const { initializeApp, cert } = require("firebase-admin/app");
const fileUploader = require("express-fileupload");
const router = require("./restful/routes");
const apiLogger = require("./helper/apiCallLog");

const serviceAccount = {
  type: "service_account",
  project_id: process.env.PROJECT_ID,
  private_key_id: process.env.PRIVATE_KEY_ID,
  private_key: process.env.PRIVATE_KEY,
  client_email: process.env.CLIENT_EMAIL,
  client_id: process.env.CLIENT_ID,
  auth_uri: "https://accounts.google.com/o/oauth2/auth",
  token_uri: "https://oauth2.googleapis.com/token",
  auth_provider_x509_cert_url: "https://www.googleapis.com/oauth2/v1/certs",
  client_x509_cert_url: process.env.CLIENT_X509_CERT_URL,
  universe_domain: "googleapis.com",
};

dotenv.config();

const PORT = process.env.PORT || 5000;
const app = express();
app.use(cors());
app.use(express.json());
initializeApp({
  credential: cert(serviceAccount),
  storageBucket: `${process.env.PROJECT_ID}.appspot.com`,
});
app.use(apiLogger);
app.use(
  fileUploader({
    fileSize: 50 * 1024 * 1024,
    useTempFiles: true,
    tempFileDir: "/tmp/",
  }),
);
app.use(router);
const start = () => {
  try {
    app.listen(PORT, () => console.log(`App listening on port ${PORT}`));
  } catch (error) {
    console.error(error.message);
    process.exit(1);
  }
};
start();
exports.api = functions.https.onRequest(app);
module.exports = app;

const admin = require("firebase-admin");
const { Util } = require("../helper/utils");
const util = new Util();
const protect = async (req, res, next) => {
  if (!req.headers.authorization) {
    util.setError(401, "Token not found");
    return util.send(res);
  }

  let token;

  try {
    token = req.headers.authorization.split(" ")[1];

    const decodedToken = await admin.auth().verifyIdToken(token);

    if (!decodedToken) {
      return res.status(401).json({ error: "Not Authenticated!" });
    }
    req.user = decodedToken;
    next();
  } catch (error) {
    return res.status(401).json({ error: "Not Authorized!" });
  }
};
exports.protect = protect;

/* eslint-disable no-prototype-builtins */
const dotenv = require("dotenv");
const admin = require("firebase-admin");
const { Util } = require("../../helper/utils");
/* eslint-disable quotes */

dotenv.config();
/**
 * @class AuthController
 * @classdesc AuthController
 */

const util = new Util();

class AdminController {
  /**
   * @param {Object} req request Object.
   * @param {Object} res response Object.
   * @returns {Object} response Object.
   */

  static async adminCreateUser(req, res) {
    const { firstName, lastName, email, role, password } = req.body;
    let uid = null;
    const db = admin.firestore();
    try {
      const user = await admin.auth().createUser({
        email,
        password,
      });

      uid = user.uid;
      await admin
        .auth()
        .setCustomUserClaims(uid, { role, emailVerified: true });
      const usersCollectionRef = db.collection("users");

      const userData = {
        uid: user.uid,
        email,
        firstName,
        lastName,
        role,
        ...((role === "creator" || role === "brand") && { subscribed: false }), // Add subscription field for admin or creators
        disabled: false,
      };
      if (role === "admin" || role === "super_admin") {
        userData.organization = db.doc("organizationInfo/ciq");
      }

      await usersCollectionRef.doc(user.uid).set(userData);

      util.statusCode = 200;
      util.setSuccess(200, "User created successfully!");
      return util.send(res);
    } catch (error) {
      if (uid) {
        const userRecord = await admin.auth().getUser(uid);
        await admin.auth().deleteUser(userRecord);
      }
      const errorMessage = error?.errorInfo?.message;
      util.statusCode = 500;
      util.message = errorMessage || error.message || "Server error";
      return util.send(res);
    }
  }

  static async adminActivateUser(req, res) {
    const { userId } = req.params;
    try {
      const db = admin.firestore();
      await admin.auth().updateUser(userId, { disabled: false });
      const usersCollectionRef = db.collection("users");
      await usersCollectionRef
        .doc(userId)
        .set({ disabled: false }, { merge: true });
      util.statusCode = 200;
      util.setSuccess(200, "User activated successfully!");
      return util.send(res);
    } catch (error) {
      console.log(error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async adminDeActivateUser(req, res) {
    const { userId } = req.params;
    try {
      const db = admin.firestore();
      await admin.auth().updateUser(userId, { disabled: true });
      const usersCollectionRef = db.collection("users");
      await usersCollectionRef
        .doc(userId)
        .set({ disabled: true }, { merge: true });
      util.statusCode = 200;
      util.setSuccess(200, "User deactivated successfully!");
      return util.send(res);
    } catch (error) {
      console.log(error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async admingetAllUsers(req, res) {
    try {
      const db = admin.firestore();
      const usersCollection = db.collection("users");

      const querySnapshot = await usersCollection.get();

      const users = [];

      if (!querySnapshot.empty) {
        querySnapshot.forEach((doc) => {
          const userObj = doc.data();
          // Only push objects with a uid field
          if (Object.hasOwn(userObj, "uid")) {
            users.push(userObj);
          }
        });
      }

      return res.status(200).json(users);
    } catch (error) {
      console.log(error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async adminDeleteUser(req, res) {
    const { userId } = req.params;
    try {
      const db = admin.firestore();
      const userDoc = await db.collection("users").doc(userId).get();
      const userData = userDoc.data();
      const profilePhoto = userData?.profilePhoto;
      await admin.auth().deleteUser(userId);
      await db.collection("users").doc(userId).delete();
      if (profilePhoto) {
        const fileName = profilePhoto.split("/").pop();
        const bucket = admin.storage().bucket();
        await bucket.file(`profile/picture/${fileName}`).delete();
      }

      util.statusCode = 200;
      util.setSuccess(200, "User deleted successfully!");
      return util.send(res);
    } catch (error) {
      console.log(error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async adminUpdateUserLimits(req, res) {
    const { userId } = req.params;
    const { maxOpportunities, max_opportunities_applied } = req.body;

    const db = admin.firestore();

    try {
      const userRef = db.collection("users").doc(userId);
      const userDoc = await userRef.get();

      if (!userDoc.exists) {
        util.statusCode = 404;
        util.message = "User not found";
        return util.send(res);
      }

      const updateData = {};

      if (maxOpportunities !== undefined) {
        updateData.maxOpportunities = maxOpportunities;
      }
      if (max_opportunities_applied !== undefined) {
        updateData.max_opportunities_applied = max_opportunities_applied;
      }

      await userRef.update(updateData);

      util.statusCode = 200;
      util.setSuccess(200, "User limits updated successfully!");
      return util.send(res);
    } catch (error) {
      console.log(error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async adminGetInfo(req, res) {
    try {
      const db = admin.firestore();
      const usersQuery = await db.collection("users").get();
      const subscribedUsersQuery = await db
        .collection("users")
        .where("subscribed", "==", true)
        .get();
      const opportunitiesQuery = await db.collection("opportunities").get();
      const completedOpportunitiesQuery = await db
        .collection("opportunities")
        .where("status", "==", "completed")
        .get();
      const infoData = [
        { title: "Users", value: `${usersQuery.size}` },
        { title: "Subscribed Users", value: `${subscribedUsersQuery.size}` },
        { title: "Opportunities", value: `${opportunitiesQuery.size}` },
        {
          title: "Completed Opportunities",
          value: `${completedOpportunitiesQuery.size}`,
        },
      ];

      util.setSuccess(200, "Admin data retirved successfully!", infoData);
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.mesage || "Server error";
      return util.send(res);
    }
  }

  static async getAllOpportunities(req, res) {
    const db = admin.firestore();

    try {
      const applicationsSnapshot = await db
        .collectionGroup("applications")
        .get();
      const opportunityApplicationsCount = {};

      // Count the number of applications for each opportunity
      applicationsSnapshot.forEach((doc) => {
        const applicationData = doc.data();
        const opportunityId = applicationData.opportunityId;
        if (opportunityApplicationsCount[opportunityId]) {
          opportunityApplicationsCount[opportunityId]++;
        } else {
          opportunityApplicationsCount[opportunityId] = 1;
        }
      });

      // Fetch opportunities separately
      const opportunitiesSnapshot = await db
        .collection("opportunities")
        .where("status", "!=", "archived")
        .get();

      // Fetch user data for each opportunity asynchronously
      const opportunitiesDataPromises = opportunitiesSnapshot.docs.map(
        async (doc) => {
          const opportunityData = doc.data();
          const userId = opportunityData.userId;
          const userRef = db.collection("users").doc(userId);
          const userDoc = await userRef.get();
          const userData = userDoc.data();
          const opportunityId = doc.id;

          return {
            ...opportunityData,
            numberOfApplications:
              opportunityApplicationsCount[opportunityId] || 0,
            fullName: userData?.firstName + " " + userData?.lastName,
          };
        },
      );

      // Wait for all promises to resolve
      const opportunitiesData1 = await Promise.all(opportunitiesDataPromises);

      if (opportunitiesData1.length > 0) {
        util.statusCode = 200;
        util.message = opportunitiesData1;
        return util.send(res);
      } else {
        util.statusCode = 404;
        util.message = "Not found";
        return util.send(res);
      }
    } catch (error) {
      console.error("Error fetching opportunities:", error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async getCompanyInfo(req, res) {
    try {
      const db = admin.firestore();
      const ciQRef = db.collection("organizationInfo").doc("ciq");
      const userDoc = await ciQRef.get();
      util.setSuccess(200, userDoc.data());
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.message || "Error fetching companiy's data";
      return util.send(res);
    }
  }

  static async updateCompanyInfo(req, res) {
    try {
      const { organizationName, organizationLogo } = req.body;
      if (!organizationName) {
        util.statusCode = 400;
        util.message = "Organization name is required.";
        return util.send(res);
      }
      const db = admin.firestore();
      const ciQRef = db.collection("organizationInfo").doc("ciq");
      const updateData = { organizationName };

      if (organizationLogo) {
        updateData.organizationLogo = organizationLogo;
      }
      await ciQRef.set(updateData, { merge: true });
      util.setSuccess(
        200,
        updateData,
        "Organization info updated successfully!",
      );
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.message || "Error updating Organization's data";
      return util.send(res);
    }
  }
}

exports.AdminController = AdminController;

const dotenv = require("dotenv");
const { Util } = require("../../helper/utils");
const admin = require("firebase-admin");
const { SendAcceptEmail } = require("../../services/templates/SendAcceptEmail");
const transporter = require("../../helper/mailHelper");
const sendNotification = require("../../helper/sendNotification");

dotenv.config();

const util = new Util();

async function createRoom(db, participantIds, opportunityTitle) {
  try {
    const [authorId, creatorId] = participantIds;
    const roomId = authorId + "_" + creatorId;
    const roomRef = db.collection("rooms").doc(roomId);
    const roomSnapshot = await roomRef.get();

    // Fetch user data for both user and creator
    const userRef = db.collection("users").doc(authorId);
    const creatorRef = db.collection("users").doc(creatorId);
    const [userSnapshot, creatorSnapshot] = await Promise.all([
      userRef.get(),
      creatorRef.get(),
    ]);

    if (!userSnapshot.exists || !creatorSnapshot.exists) {
      throw new Error("The author or creator could not be found");
    }

    const authorData = userSnapshot.data();
    const creatorData = creatorSnapshot.data();

    if (roomSnapshot.exists) {
      const existingRoomData = roomSnapshot.data();
      return {
        status: 200,
        roomId,
        room: existingRoomData,
      };
    }

    const roomData = {
      id: roomId,
      participantIds,
      lastMessage: "",
      opportunityTitle,
      createdAt: admin.firestore.FieldValue.serverTimestamp(),
      userProfiles: [
        {
          userId: authorId,
          fullName: `${authorData.firstName} ${authorData.lastName}`,
          profilePhoto: authorData.profilePhoto || "",
        },
        {
          userId: creatorId,
          fullName: `${creatorData.firstName} ${creatorData.lastName}`,
          profilePhoto: creatorData.profilePhoto || "",
        },
      ],
    };

    await roomRef.set(roomData);

    return {
      status: 200,
      roomId,
      room: roomData,
    };
  } catch (error) {
    console.error("Error creating room:", error);
  }
}

class ApplicationsController {
  static async getAllApplications(req, res) {
    const db = admin.firestore();
    try {
      const applicationsData = [];

      // Fetch all documents from the "applications" collection
      const querySnapshot = await db.collection("applications").get();

      // Iterate over each document
      querySnapshot.forEach((doc) => {
        const data = doc.data();
        applicationsData.push(data);
      });

      util.statusCode = 200;
      util.message = applicationsData;
      return util.send(res);
    } catch (error) {
      console.log(error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async getApplicationById(req, res) {
    const db = admin.firestore();
    const { applicationId } = req.params;
    try {
      console.log("Fetching application with ID:", applicationId); // Add this line for logging

      const applicationSnapshot = await db
        .collection("applications")
        .doc(applicationId)
        .get();

      if (applicationSnapshot.exists) {
        const applicationData = applicationSnapshot.data();
        util.statusCode = 200;
        util.message = applicationData;
        return util.send(res);
      } else {
        console.log("Application not found:", applicationId); // Add this line for logging
        util.statusCode = 404;
        util.message = "Application not found";
        return util.send(res);
      }
    } catch (error) {
      console.error("Error fetching application:", error); // Add this line for logging
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async createApplication(req, res) {
    const db = admin.firestore();
    const { authorId, opportunityId, proposal, creatorId } = req.body;
    try {
      // Fetch the user document
      const creatorRef = db.collection("users").doc(creatorId);
      const authorDoc = await creatorRef.get();

      if (!authorDoc.exists) {
        util.statusCode = 404;
        util.message = "User not found";
        return util.send(res);
      }

      const authorData = authorDoc.data();

      // Check the number of applications made by the creator
      if (
        authorData.opportunities_applied_count >=
        authorData.max_opportunities_applied
      ) {
        util.statusCode = 400;
        util.message = `You can only apply to up to ${authorData.max_opportunities_applied} opportunities.`;
        return util.send(res);
      }

      const applicationRef = db.collection("applications").doc();
      const newApplicationData = {
        applicationId: applicationRef.id,
        authorId,
        opportunityId,
        proposal,
        creatorId,
        status: "pending",
      };
      await applicationRef.set(newApplicationData);

      // Increment the opportunities_applied_count for the user
      await creatorRef.update({
        opportunitiesAppliedCount: admin.firestore.FieldValue.increment(1),
      });

      util.statusCode = 201;
      util.message = newApplicationData;
      return util.send(res);
    } catch (error) {
      console.log(error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async updateApplication(req, res) {
    const db = admin.firestore();
    const { applicationId } = req.params;
    const { status, authorId, creatorId, opportunityTitle } = req.body;

    try {
      const applicationRef = db.collection("applications").doc(applicationId);

      if (!status) {
        util.statusCode = 400;
        util.message = "Status field is required for updating an application";
        return util.send(res);
      }

      await applicationRef.update({ status });

      if (status === "accepted") {
        const participantIds = [authorId, creatorId];

        // Call createRoom function with data
        const { roomId } = await createRoom(
          db,
          participantIds,
          opportunityTitle,
        );

        const userRef = db.collection("users").doc(authorId);
        const doc = await userRef.get();
        if (doc.exists) {
          const { firstName, email, fcmToken, uid } = doc.data();
          if (email) {
            const emailTemplate = SendAcceptEmail(firstName);

            const mailOptions = {
              from: process.env.EMAIL,
              to: email,
              subject: "Creator Platform Application Update",
              html: emailTemplate,
            };

            await transporter.sendMail(mailOptions);
          }
          if (fcmToken) {
            const notificationData = {
              token: fcmToken,
              title: "Your Application Update",
              body: "Your Application has been approved!",
              userId: uid,
            };
            await sendNotification(notificationData);
          }
        }

        util.statusCode = 200;
        util.message = { roomId };
        return util.send(res);
      }

      util.statusCode = 200;
      util.message = "Application status updated successfully";
      return util.send(res);
    } catch (error) {
      console.error("Error updating application:", error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async deleteApplication(req, res) {
    const db = admin.firestore();
    const { applicationId } = req.params;
    try {
      await db.collection("applications").doc(applicationId).delete();
      util.statusCode = 200;
      util.message = "Application deleted successfully";
      return util.send(res);
    } catch (error) {
      console.log(error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async getAllApplicationsById(req, res) {
    const { opportunityId } = req.params;

    try {
      const db = admin.firestore();
      const applicationsRef = db.collection("applications");

      // Query applications where opportunityId matches
      const querySnapshot = await applicationsRef
        .where("opportunityId", "==", opportunityId)
        .get();

      const applications = [];

      console.log(`Query returned ${querySnapshot.size} documents`);

      if (!querySnapshot.empty) {
        // Extract application data from query snapshot
        querySnapshot.forEach((doc) => {
          console.log(`Found application: ${JSON.stringify(doc.data())}`);
          applications.push({ id: doc.id, ...doc.data() });
        });
      } else {
        console.log(
          `No applications found for opportunityId: ${opportunityId}`,
        );
      }

      return res.status(200).json(applications);
    } catch (error) {
      console.error(
        `Error fetching applications for opportunityId: ${opportunityId}`,
        error,
      );
      return res
        .status(500)
        .json({ message: "Server error", error: error.message });
    }
  }
}

exports.ApplicationsController = ApplicationsController;

/* eslint-disable no-prototype-builtins */
const dotenv = require("dotenv");
const { Util } = require("../../helper/utils");
const SendPasswordReset = require("../../services/templates/SendPasswordReset");
const transporter = require("../../helper/mailHelper");
const admin = require("firebase-admin");
const { v4: uuidv4 } = require("uuid");
const jwt = require("jsonwebtoken");
const Joi = require("joi");

dotenv.config();
/**
 * @class AuthController
 * @classdesc AuthController
 */

const util = new Util();

const defaultSchema = {
  firstName: Joi.string(),
  lastName: Joi.string(),
  email: Joi.string(),
  password: Joi.string(),
  bio: Joi.string(),
  goals: Joi.string(),
  role: Joi.string(),
};

// Validation schema for creator
const schema = {
  brand: Joi.object({
    ...defaultSchema,
    organizationName: Joi.string(),
    profilePhoto: Joi.string().allow(""),
  }),
  creator: Joi.object({
    ...defaultSchema,
    podcastName: Joi.string().allow(""),
    podcastUrl: Joi.string().uri().allow(""),
    profilePhoto: Joi.string().allow(""),
    showreel: Joi.string().uri().allow(""),
    showcase: Joi.array().items(Joi.string().uri().max(6)).allow(""),
    credits: Joi.array().items(
      Joi.object({
        episodeLink: Joi.string(),
        role: Joi.string(),
      }).allow(""),
    ),
  }),
};

class AuthController {
  /**
   * @param {Object} req request Object.
   * @param {Object} res response Object.
   * @returns {Object} response Object.
   */

  static async signup(req, res) {
    try {
      // Proceed with signup logic if validation succeeds
      const { firstName, lastName, email, password, role, ...other } = req.body;

      // Validate request body against schema
      await schema[role].validateAsync(req.body);

      const db = admin.firestore();
      let user = null;

      // Create user in Firebase Authentication
      user = await admin.auth().createUser({
        email,
        password,
      });

      const uid = user.uid;
      await admin.auth().setCustomUserClaims(uid, { role, subscribed: false });

      const usersCollectionRef = db.collection("users");

      await usersCollectionRef.doc(user.uid).set({
        uid: user.uid,
        firstName,
        lastName,
        role,
        email,
        disabled: false,
        subscribed: false,
        ...other,
      });

      util.statusCode = 200;
      util.setSuccess(200, "Success", { email, uid });
      return util.send(res);
    } catch (error) {
      console.log(error);
      const errorMessage = error?.errorInfo?.message;
      util.statusCode = 500;
      util.message = errorMessage || error.message || "Server error";
      return util.send(res);
    }
  }

  static async verifyOtp(req, res) {
    try {
      const { email, otp, uid } = req.body;
      const db = admin.firestore();

      // Retrieve OTP document
      const otpDoc = await db.collection("otp").doc(email).get();
      const savedOTP = otpDoc.data()?.otp;

      if (savedOTP !== otp) {
        util.statusCode = 400;
        util.message = "Invalid OTP";
        return util.send(res);
      }
      const currentClaims =
        (await admin.auth().getUser(uid)).customClaims || {};
      const updatedClaims = {
        ...currentClaims,
        emailVerified: true,
      };
      await admin.auth().setCustomUserClaims(uid, updatedClaims);
      await db.collection("otp").doc(email).delete();

      util.statusCode = 200;
      util.message = "Your account has been successfully verified.";
      return util.send(res);
    } catch (error) {
      console.error("Error verifying OTP:", error);
      const errorMessage =
        error?.errorInfo?.message || error.message || "Server error";
      util.statusCode = 500;
      util.message = errorMessage;
      return util.send(res);
    }
  }

  static async forgetPassword(req, res) {
    try {
      const { email } = req.body;
      const userRecord = await admin.auth().getUserByEmail(email);
      if (userRecord.email !== email) {
        return res.status(404).json({ res: "This email is not registered" });
      }
      const secret = process.env.JWT_SECRET + userRecord.uid;
      const payload = {
        uid: userRecord.uid,
      };
      const token = jwt.sign(payload, secret, { expiresIn: "15m" });
      const link = `${process.env.DOMAIN}/reset-password?token=${token}`;
      const mailOptions = {
        from: process.env.EMAIL,
        to: email,
        subject: "Password Reset Request for Your Creator Platform Account",
        html: SendPasswordReset(link),
      };
      await transporter.sendMail(mailOptions);
      return res
        .status(200)
        .json({ message: "Password reset email sent successfully" });
    } catch (error) {
      return res.status(500).json({ message: error.message || "Server error" });
    }
  }

  static async resetUserPassword(req, res) {
    const { password, uid } = req.body;
    try {
      await admin.auth().updateUser(uid, {
        password,
      });
      util.statusCode = 200;
      util.message = "Password updated succesfully";
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async getUser(req, res) {
    const { user_id, role } = req.user;
    const db = admin.firestore();
    try {
      const docRef = db.collection("users").doc(user_id);
      const docSnapshot = await docRef.get();

      if (docSnapshot.exists) {
        const userData = docSnapshot.data();
        if (role === "super_admin" || role === "admin") {
          const organizationInfoRef = userData?.organization;
          if (organizationInfoRef) {
            const organizationInfoSnapshot = await organizationInfoRef?.get();
            if (organizationInfoSnapshot.exists) {
              userData.organizationName =
                organizationInfoSnapshot.data().organizationName;
              userData.organizationLogo =
                organizationInfoSnapshot.data().organizationLogo;
            }
          }
        }
        const { organization, ...filteredData } = userData;
        const dataToReturn = { ...filteredData };
        return res.status(200).json(dataToReturn);
      } else {
        return res.status(404).json({ message: "No such document!" });
      }
    } catch (error) {
      console.error("Error fetching user:", error);
      return res.status(500).json({ message: error.message || "Server error" });
    }
  }

  static async getPublicUser(req, res) {
    try {
      const { userId } = req.params;

      if (!userId) {
        util.statusCode = 400;
        util.message = "User ID is required";
        return util.send(res);
      }

      const db = admin.firestore();
      const usersCollection = db.collection("users").doc(userId);

      const querySnapshot = await usersCollection.get();
      if (!querySnapshot.exists) {
        util.statusCode = 404;
        util.message = "User not found";
        return util.send(res);
      }

      const userData = querySnapshot.data();
      if (userData.role === "super_admin" || userData.role === "admin") {
        const organizationInfoRef = userData?.organization;
        if (organizationInfoRef) {
          const organizationInfoSnapshot = await organizationInfoRef?.get();
          if (organizationInfoSnapshot.exists) {
            userData.organizationName =
              organizationInfoSnapshot.data().organizationName;
            userData.organizationLogo =
              organizationInfoSnapshot.data().organizationLogo;
          }
        }
      }
      const nonSensitiveData = {
        firstName: userData.firstName,
        lastName: userData.lastName,
        role: userData.role,
        profilePhoto: userData.profilePhoto,
        bio: userData.bio,
        uid: userData.uid,
        ...(userData.organizationName
          ? { organizationName: userData.organizationName }
          : {}),
        ...(userData.organizationLogo
          ? { organizationLogo: userData.organizationLogo }
          : {}),
        meta: {
          ...(userData.role === "creator"
            ? { showcase: userData?.showcase, credits: userData?.credits }
            : {}),
        },
      };

      util.statusCode = 200;
      util.message = nonSensitiveData;
      return util.send(res);
    } catch (error) {
      console.error("Error fetching non-sensitive profile:", error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async getAllUsers(req, res) {
    try {
      const db = admin.firestore();
      const usersCollection = db.collection("users");
      const querySnapshot = await usersCollection.get();
      const users = [];

      if (!querySnapshot.empty) {
        for (const doc of querySnapshot.docs) {
          const userObj = doc.data();
          if (userObj.role === "super_admin" || userObj.role === "admin") {
            const organizationInfoRef = userObj?.organization;
            if (organizationInfoRef) {
              const organizationInfoSnapshot = await organizationInfoRef?.get();
              if (organizationInfoSnapshot.exists) {
                userObj.organizationName =
                  organizationInfoSnapshot.data().organizationName;
                userObj.organizationLogo =
                  organizationInfoSnapshot.data().organizationLogo;
              }
            }
          }
          const { organization, ...filteredData } = userObj;
          users.push(filteredData);
        }
      }

      return res.status(200).json(users);
    } catch (error) {
      return res.status(500).json({
        statusCode: 500,
        message: error.message || "Server error",
      });
    }
  }

  static async updateUser(req, res) {
    try {
      const { ...valuesToUpdate } = req.body;
      const file = req.files?.profilePhoto;

      if (!file) {
        // If there's no file, update user data directly in Firestore
        const docRef = admin
          .firestore()
          .collection("users")
          .doc(req.user.user_id);
        await docRef.set({ ...valuesToUpdate }, { merge: true });

        util.statusCode = 200;
        util.message = "User updated successfully";
        return util.send(res);
      } else {
        // If there's a file, upload it to Firebase Storage
        const storageRef = admin.storage().bucket();
        const fileName = `profile/picture/${uuidv4()}_${file.name}`;
        const uploadTask = storageRef.upload(file.tempFilePath, {
          public: true,
          destination: fileName,
          metadata: {
            firebaseStorageDownloadTokens: uuidv4(),
          },
        });

        uploadTask
          .then(async (snapshot) => {
            // Once uploaded, get the image URL and update user data in Firestore
            const profilePhoto = snapshot[0].metadata.mediaLink;
            const docRef = admin
              .firestore()
              .collection("users")
              .doc(req.user.user_id);

            await docRef.set(
              {
                profilePhoto,
                ...valuesToUpdate,
              },
              { merge: true },
            );

            util.setSuccess(200, "User profile updated successfully", {
              profilePhoto,
            });

            return util.send(res);
          })
          .catch((error) => {
            // Handle errors during file upload
            console.error("Error uploading image:", error);
            util.statusCode = 500;
            util.message = "Failed to upload image";
            return util.send(res);
          });
      }
    } catch (error) {
      util.statusCode = 500;
      util.message = "Server error";
      return util.send(res);
    }
  }

  static async changePassword(req, res) {
    const { password } = req.body;
    try {
      await admin.auth().updateUser(req.user?.user_id, {
        password,
      });
      util.statusCode = 200;
      util.message = "Password updated succesfully";
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async checkEmailExists(req, res) {
    const { email } = req.query;

    if (!email) {
      return res.status(400).json({ message: "Email is required" });
    }

    try {
      const userRecord = await admin.auth().getUserByEmail(email);
      if (userRecord) {
        return res.status(200).json({ exists: true });
      }
    } catch (error) {
      if (error.code === "auth/user-not-found") {
        return res.status(200).json({ exists: false });
      }
      return res.status(500).json({ message: error.message || "Server error" });
    }
  }

  static async changeEmail(req, res) {
    const { email } = req.body;
    const { userId } = req.user;
    try {
      if (email !== req.user.email) {
        const docRef = admin
          .firestore()
          .collection("users")
          .doc(req.user.user_id);
        await docRef.set({ email }, { merge: true });
        await admin.auth().updateUser(userId, {
          email,
        });
      }
      util.statusCode = 200;
      util.message = "Email changed successfully";
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async updateUserSubscription(req, res) {
    try {
      const { subscribed } = req.body;
      const { userId } = req.params; // Assuming you have access to the user's ID
      const docRef = admin.firestore().collection("users").doc(userId); // Use the user's ID to locate the document in the users collection

      await docRef.set({ subscribed }, { merge: true }); // Update the 'subscribed' field

      util.statusCode = 200;
      util.message = "User subscribed status updated successfully";
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async checkSubscription(req, res) {
    try {
      const { userId } = req.params;

      if (!userId) {
        util.statusCode = 400;
        util.message = "User ID is required";
        return util.send(res);
      }

      const db = admin.firestore();
      const docRef = db.collection("users").doc(userId);
      const docSnapshot = await docRef.get();

      if (!docSnapshot.exists) {
        util.statusCode = 404;
        util.message = "User not found";
        return util.send(res);
      }

      const userData = docSnapshot.data();
      const isSubscribed = userData.subscribed || false;

      util.statusCode = 200;
      util.message = { subscribed: isSubscribed };
      return util.send(res);
    } catch (error) {
      console.error("Error checking subscription:", error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }
}

