const dotenv = require("dotenv");
const { Util } = require("../../helper/utils");
/* eslint-disable quotes */
const admin = require("firebase-admin");

dotenv.config();
/**
 * @class AuthController
 * @classdesc AuthController
 */

const util = new Util();

class NotificationsController {
  /**
   * @param {Object} req request Object.
   * @param {Object} res response Object.
   * @returns {Object} response Object.
   */

  static async sendNotification(req, res) {
    const { token, title, body, userId } = req.body;
    try {
      await admin.messaging().send({
        token,
        notification: {
          title,
          body,
        },
      });

      const notificationData = {
        title,
        body,
        timestamp: admin.firestore.FieldValue.serverTimestamp(),
      };

      await admin
        .firestore()
        .collection("users")
        .doc(userId)
        .collection("notifications")
        .add(notificationData);

      util.statusCode = 200;
      util.message = "Notification sent successfully";
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.message || "Failed to send verification email";
      return util.send(res);
    }
  }

  static async saveFcmToken(req, res) {
    const { fcmToken, userId } = req.body;
    try {
      const userRef = admin.firestore().collection("users").doc(userId);
      await userRef.set({ fcmToken }, { merge: true });

      util.statusCode = 200;
      util.message = "FCM token saved successfully";
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.message || "Failed to save FCM token";
      return util.send(res);
    }
  }

  static async getAllNotifications(req, res) {
    const { userId } = req.user;
    const notificationsRef = admin
      .firestore()
      .collection("users")
      .doc(userId)
      .collection("notifications");

    try {
      // Attempt to fetch from cache first
      const cacheSnapshot = await notificationsRef
        .orderBy("timestamp", "desc")
        .get({ source: "cache" });
      let notifications = cacheSnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));

      if (notifications.length > 0) {
        // Cache exists, check if it's up-to-date
        const serverSnapshot = await notificationsRef
          .orderBy("timestamp", "desc")
          .limit(1)
          .get();
        if (!serverSnapshot.empty) {
          const latestServerNotification = serverSnapshot.docs[0];
          const latestServerTimestamp =
            latestServerNotification.updateTime ||
            latestServerNotification.createTime;
          const latestCacheTimestamp =
            cacheSnapshot.docs[0].updateTime ||
            cacheSnapshot.docs[0].createTime;

          if (latestServerTimestamp.isEqual(latestCacheTimestamp)) {
            util.setSuccess(
              200,
              "Notifications retrieved successfully",
              notifications,
            );
            return util.send(res);
          }
        }
      }

      const freshSnapshot = await notificationsRef
        .orderBy("timestamp", "desc")
        .get();
      notifications = freshSnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));

      util.setSuccess(
        200,
        "Notifications retrieved successfully",
        notifications,
      );
      return util.send(res);
    } catch (error) {
      console.error("Error fetching notifications:", error);
      util.statusCode = 500;
      util.message = error.message || "Failed to retrieve notifications";
      return util.send(res);
    }
  }

  static async clearAllNotifications(req, res) {
    const { userId } = req.user;

    try {
      const snapshot = await admin
        .firestore()
        .collection("users")
        .doc(userId)
        .collection("notifications")
        .get();

      const batch = admin.firestore().batch();
      snapshot.docs.forEach((doc) => batch.delete(doc.ref));

      await batch.commit();

      util.statusCode = 200;
      util.message = "All notifications cleared successfully";
      return util.send(res);
    } catch (error) {
      util.statusCode = 500;
      util.message = error.message || "Failed to clear notifications";
      return util.send(res);
    }
  }
}

exports.NotificationsController = NotificationsController;

const dotenv = require("dotenv");
const { Util } = require("../../helper/utils");
const admin = require("firebase-admin");
const { v4: uuidv4 } = require("uuid"); // Import uuidv4 directly

dotenv.config();
/**
 * @class OpportunitiesController
 * @classdesc OpportunitiesController
 */

const util = new Util();
class OpportunitiesController {
  /**
   * @param {Object} req request Object.
   * @param {Object} res response Object.
   * @returns {Object} response Object.
   */

  static async getAllOpportunities(req, res) {
    const db = admin.firestore();
    const { limit = 10, startAfter: startAfterId = null } = req.query; // Default limit to 10, startAfter to null

    try {
      const opportunitiesData = [];
      let query = db
        .collection("opportunities")
        .where("status", "!=", "archived")
        .orderBy("status")
        .limit(parseInt(limit));

      // If startAfterId is provided, use it to fetch the next set of documents
      if (startAfterId) {
        const startAfterDoc = await db
          .collection("opportunities")
          .doc(startAfterId)
          .get();
        if (startAfterDoc.exists) {
          query = query.startAfter(startAfterDoc);
        } else {
          return res.status(400).json({ message: "Invalid startAfter ID" });
        }
      }

      // Fetch the documents
      const querySnapshot = await query.get();

      querySnapshot.forEach((doc) => {
        opportunitiesData.push({ id: doc.id, ...doc.data() });
      });

      if (opportunitiesData.length > 0) {
        util.statusCode = 200;
        util.message = {
          opportunities: opportunitiesData,
          nextStartAfterId: opportunitiesData[opportunitiesData.length - 1].id,
        };
        return util.send(res);
      } else {
        util.statusCode = 404;
        util.message = "No opportunities found";
        return util.send(res);
      }
    } catch (error) {
      console.error("Error fetching opportunities:", error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async getAllOpportunitiesByUserId(req, res) {
    const db = admin.firestore();
    const { userId } = req.params;
    const { limit = 10, startAfter: startAfterId = null } = req.query;

    if (!userId) {
      return res.status(400).json({ message: "User ID is required" });
    }

    try {
      const opportunitiesData = [];
      let query = db
        .collection("opportunities")
        .where("userId", "==", userId)
        .limit(parseInt(limit));

      if (startAfterId) {
        const startAfterDoc = await db
          .collection("opportunities")
          .doc(startAfterId)
          .get();
        if (startAfterDoc.exists) {
          query = query.startAfter(startAfterDoc);
        } else {
          return res.status(400).json({ message: "Invalid startAfter ID" });
        }
      }

      const querySnapshot = await query.get();

      querySnapshot.forEach((doc) => {
        if (doc.data().status !== "archived") {
          opportunitiesData.push({ id: doc.id, ...doc.data() });
        }
      });

      if (opportunitiesData.length > 0) {
        return res.status(200).json({
          opportunities: opportunitiesData,
          nextStartAfterId: opportunitiesData[opportunitiesData.length - 1].id,
        });
      } else {
        return res.status(404).json({ message: "No opportunities found" });
      }
    } catch (error) {
      console.error("Error fetching opportunities by user ID:", error);
      return res.status(500).json({ message: "Server error" });
    }
  }

  static async getOpportunityById(req, res) {
    const db = admin.firestore();
    const { opportunityId } = req.params;

    if (!opportunityId) {
      return res.status(400).json({ message: "Opportunity ID is required" });
    }

    try {
      const opportunityRef = db.collection("opportunities").doc(opportunityId);
      const docSnapshot = await opportunityRef.get();

      if (!docSnapshot.exists) {
        return res.status(404).json({ message: "Opportunity not found" });
      }

      const opportunityData = docSnapshot.data();
      return res.status(200).json(opportunityData);
    } catch (error) {
      console.error("Error fetching opportunity by ID:", error);
      return res.status(500).json({ message: "Server error" });
    }
  }

  static async getOpportunitiesByStatus(req, res) {
    const db = admin.firestore();
    const { status } = req.params;
    const { limit = 10, startAfter: startAfterId = null } = req.query;

    if (!status || !["open", "in_progress", "completed"].includes(status)) {
      util.statusCode = 400;
      util.message = "Invalid or missing opportunity status";
      return util.send(res);
    }

    try {
      const opportunitiesData = [];
      let query = db
        .collection("opportunities")
        .where("status", "==", status)
        .limit(parseInt(limit));

      if (startAfterId) {
        const startAfterDoc = await db
          .collection("opportunities")
          .doc(startAfterId)
          .get();
        if (startAfterDoc.exists) {
          query = query.startAfter(startAfterDoc);
        } else {
          util.statusCode = 400;
          util.message = "Invalid startAfter ID";
          return util.send(res);
        }
      }

      const querySnapshot = await query.get();

      querySnapshot.forEach((doc) => {
        opportunitiesData.push({ id: doc.id, ...doc.data() });
      });

      if (opportunitiesData.length > 0) {
        util.statusCode = 200;
        util.message = {
          opportunities: opportunitiesData,
          nextStartAfterId: opportunitiesData[opportunitiesData.length - 1].id,
        };
        return util.send(res);
      } else {
        util.statusCode = 404;
        util.message = "No opportunities found";
        return util.send(res);
      }
    } catch (error) {
      console.error("Error fetching opportunities by status:", error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async deleteOpportunityById(req, res) {
    const db = admin.firestore();
    try {
      const { opportunityId } = req.params;

      if (!opportunityId) {
        return res.status(400).json({ message: "Opportunity ID is required" });
      }

      // Fetch the opportunity document from Firestore
      const opportunityRef = db.collection("opportunities").doc(opportunityId);
      const docSnapshot = await opportunityRef.get();

      if (!docSnapshot.exists) {
        return res.status(404).json({ message: "Opportunity not found" });
      }

      // Update the status of the opportunity to "archived"
      await opportunityRef.update({ status: "archived" });

      return res
        .status(200)
        .json({ message: "Opportunity archived successfully" });
    } catch (error) {
      console.error("Error archiving opportunity:", error);
      return res.status(500).json({ message: "Server error" });
    }
  }

  static async updateOpportunityById(req, res) {
    const db = admin.firestore();
    try {
      const { opportunityId } = req.params;
      const { type } = req.body;

      // Fetch the opportunity document
      const opportunityRef = db.collection("opportunities").doc(opportunityId);
      const opportunitySnapshot = await opportunityRef.get();

      // Check if the opportunity exists
      if (!opportunitySnapshot.exists) {
        return res.status(404).json({ message: "Opportunity not found" });
      }

      // Validate type field
      if (!type || !["job", "pitch", "campaign"].includes(type)) {
        throw new Error("Invalid or missing opportunity type");
      }

      // Get required fields based on the type
      const requiredFields = getTypeRequiredFields(type);

      // Prepare the update object with only provided fields
      const updateData = {};
      requiredFields.forEach((field) => {
        // Check if the field is provided in the request body
        if (Object.hasOwn(req.body, field)) {
          updateData[field] = req.body[field];
        }
      });

      // Perform the update
      await opportunityRef.update(updateData);

      // Return success response
      return res
        .status(200)
        .json({ message: "Opportunity updated successfully", statusCode: 200 });
    } catch (error) {
      console.log(error);
      console.error("Error updating opportunity:", error);
      return res
        .status(500)
        .json({ message: error?.message || "Server error", statusCode: 500 });
    }
  }

  static async createOpportunity(req, res, type) {
    const db = admin.firestore();
    try {
      const { userId } = req.body;

      // Fetch the user document
      const userRef = db.collection("users").doc(userId);
      const userDoc = await userRef.get();

      if (!userDoc.exists) {
        util.statusCode = 404;
        util.message = "User not found";
        return util.send(res);
      }

      const userData = userDoc.data();

      // Check the number of opportunities posted by the brand
      if (userData.opportunitiesPostedCount >= userData.maxOpportunities) {
        util.statusCode = 400;
        util.message = `You can only post up to ${userData.maxOpportunities} opportunities.`;
        return util.send(res);
      }

      // Generate UUID for opportunityId
      const opportunityId = uuidv4();

      // Extract opportunity data from request body
      const { ...opportunityData } = req.body;

      // Set default status to "open" if not provided
      if (!Object.hasOwn(opportunityData, "status")) {
        opportunityData.status = "open";
      }

      // If company details are not provided, use organizationName from user data
      if (!opportunityData.company) {
        opportunityData.company = userData.organizationName || null;
      }

      // Add profilePhoto of the user who created the opportunity
      if (userData.profilePhoto) {
        opportunityData.profilePhoto = userData.profilePhoto;
      }

      // Validate required fields
      const requiredFields = getTypeRequiredFields(type);
      const isValid = requiredFields.every((field) =>
        Object.hasOwn(opportunityData, field),
      );
      if (!isValid) {
        util.statusCode = 400;
        util.message = "Please fill in all required fields";
        return util.send(res);
      }

      // Check if opportunity with same ID already exists
      const existingOpportunity = await db
        .collection("opportunities")
        .doc(opportunityId)
        .get();
      if (existingOpportunity.exists) {
        util.statusCode = 400;
        util.message = "Opportunity with same ID already exists";
        return util.send(res);
      }

      // Store the opportunity data in the opportunities collection
      await db
        .collection("opportunities")
        .doc(opportunityId)
        .set({
          opportunityId,
          type,
          ...opportunityData,
        });

      // Increment the opportunitiesPostedCount for the user
      await userRef.update({
        opportunitiesPostedCount: admin.firestore.FieldValue.increment(1),
      });

      util.statusCode = 201;
      util.message = "Opportunity created successfully";
      return util.send(res);
    } catch (error) {
      console.error(error);
      util.statusCode = 500;
      util.message = error.message || "Server error";
      return util.send(res);
    }
  }

  static async createJobOpportunity(req, res) {
    return OpportunitiesController.createOpportunity(req, res, "job");
  }

  static async createPitchOpportunity(req, res) {
    return OpportunitiesController.createOpportunity(req, res, "pitch");
  }

  static async createCampaignOpportunity(req, res) {
    return OpportunitiesController.createOpportunity(req, res, "campaign");
  }
}

const requiredFields = ["title", "description", "userId"];

function getTypeRequiredFields(type) {
  switch (type) {
    case "job":
      return [
        ...requiredFields,
        "category",
        "contractType",
        "location",
        "company",
        "companyWebsite",
        "companyDescription",
        "companyContactName",
        "companyContactEmail",
        "companyContactTel",
        "experience",
        "skills",
        "education",
        "salary",
        "terms",
        "deadline",
      ];
    case "pitch":
      return [
        ...requiredFields,
        "targetAudience",
        "contentDuration",
        "contentType",
        "keyMessage",
      ];
    case "campaign":
      return [
        ...requiredFields,
        "targetAudience",
        "targetDemographic",
        "compensation",
        "adType",
        "length",
        "budget",
        "startDate",
        "endDate",
      ];
    default:
      return [];
  }
}

exports.OpportunitiesController = OpportunitiesController;

